func GetRandomQuote() (string, string) {
	if len(SongQuotes) == 0 {
		return "–¶–∏—Ç–∞—Ç—ã –Ω–µ –Ω–∞–π–¥–µ–Ω—ã", "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –ø–µ—Å–Ω—è"
	}

	quotes := make([]string, 0, len(SongQuotes))
	for quote := range SongQuotes {
		quotes = append(quotes, quote)
	}

	if len(quotes) == 0 {
		return "–ú–∞—Å—Å–∏–≤ —Ü–∏—Ç–∞—Ç –ø—É—Å—Ç", "–ù–µ–∏–∑–≤–µ—Å—Ç–Ω–∞—è –ø–µ—Å–Ω—è"
	}

	randomQuote := quotes[rand.Intn(len(quotes))]
	songName := SongQuotes[randomQuote]

	return randomQuote, songName
}

