func GetRandomClip() map[string]string {
	result := make(map[string]string)
	keys := make([]string, 0, len(clipScreensDirs))
	for k := range clipScreensDirs {
		keys = append(keys, k)
	}
	if len(keys) == 0 {
		return result
	}
	randomIdx := rand.Intn(len(keys))
	key := keys[randomIdx]
	result[key] = clipScreensDirs[key]
	return result
}

